// 根据屏幕宽度适配字体大小
$(window).on('resize', function () {
  var width = Math.min(document.documentElement.clientWidth, 640);
  $('html').css('font-size', width / 640 * 32 + 'px').removeClass('');
}).triggerHandler('resize');

// 通知
var notice = (function () {
  var timer = null;
  var lasted = 4000;
  var instance = null;

  var hanlder = function (message, type) {
    if (instance === null) {
      instance = $('<div class="js-notice" />').appendTo('body');
    }
    type = type || 'normal';
    instance.removeClass('overdue').html('<span class="' + type + '">' + message + '</span>');
    clearTimeout(timer);
    timer = setTimeout(function () {
      instance.addClass('overdue');
    }, lasted);
  };

  // 添加快捷方式
  ['success', 'warning', 'failure'].forEach(function (name) {
    hanlder[name] = function (message) {
      hanlder(message, name);
    };
  });

  return hanlder;
})();